import { useEffect, useState } from "react"

const axios = require('axios');
function Patients(){

     const [mainPatients ,setPatients ] = useState(null)


    const getPatients =()=>{
        axios.get('http://localhost:3001/patients')
            .then(function (response) {
          
             console.log("res", response.data);
             setPatients(response.data)
              })
             .catch(function (error) {
           
            console.log(error);
            })
      }

      useEffect(()=>{
          getPatients()
      },[])


    return<div>
        
        <button onClick={()=>{
            axios.get('http://localhost:3001/patients?_sort=Salary&_order=desc')
            .then(function (response) {
            
              console.log(response.data);
              setPatients(response.data)
            })
            .catch(function (error) {
           
              console.log(error);
            })
        }}
        
        >Sort-Salary </button>
      
         {mainPatients?.map((el, i)=> 
         
         <div key={i} style={{
             border : "1px solid black",
             padding : "20px",
             margin : "20px"
            
         }}>
             
             <h2>Name : {el?.name}</h2>
             <h5>Age : {el?.age}</h5>
             
             <h5>Gender : {el?.gender} </h5>
             <h5>Madicens Priscripted : {el?.medicines}</h5>
             
             <button  onClick={()=>{
                 axios.put('http://localhost:3001/patients', {
                    name : el?.name,
                    age: el?.age,
                    
                    Gender : el?.gender,
                   
                 })
                  .then(function (response) {
                    console.log(response);
                    getPatients()
                  })
                  .catch(function (error) {
                    console.log(error);
                  });
                

             }}
                 
                  
                  
                 
              > </button>
         </div>
         
         
         )}
          
    </div>
}

export default Patients
