import React from 'react'

   export default function SignIn(props) {
  return (
    <div className="container">
        <h3>Sing In to Start Work</h3>
      <form>
  <div className="mb-3">
    <label for="exampleInputEmail1" className="form-label">Email</label>
    <input type="email" className="form-control"  id="exampleInputEmail1" aria-describedby="emailHelp"/>
  </div>
  <div className="mb-3">
    <label for="exampleInputPassword1" className="form-label">Password</label>
    <input type="password" className="form-control"   id="exampleInputPassword1"/>
  </div>
  
  <button type="submit" className="btn btn-sm btn-primary" >Submit</button>
</form>
    </div>
  )
}
import { useContext, useState } from "react"
import { useNavigate } from "react-router-dom"
import { AuthContext } from "../Context/Token"



const Login =()=>{

    const navigate = useNavigate()
    const [login,setlogin] = useState({    })
    
    const handleChange =(e)=>{
        console.log(e.target.value,e.target.name)
        const {name,value} = e.target
        setlogin({...login,[name]:value })
    }

   const {handleToken}= useContext(AuthContext)

    return<div className="container">
        <h1>Login</h1>

        <label>Name: </label>
        <input name="Name" type="text"  placeholder="enter name" onChange={handleChange}/>
        <br/>
        <br/>
        <label>Password : </label>
        <input name="Password" type="text" placeholder="enter password" onChange={handleChange}/>
        <br/>
        <br/>
        <button onClick={(e)=>{
             e.preventDefault()
             console.log(login)
          
           if(login.Name === "admin" && login.Password === "admin"){
          
               handleToken("12345")
               navigate("/Admin")
           }else{
               navigate("/Applyjob")
           }

        }} className="btn btn-sm btn-primary">Submit</button>
    </div>
}

export {Login}
