import { useEffect, useState } from "react"
import { useSelector } from "react-redux"
const axios = require('axios');
const Records =()=>{
  const [page,setPage] = useState(1)
     const [text ,setText ] = useState(null)


    const details =()=>{
        axios.get(`http://localhost:3001/student?_page=${page}&_limit=3`)
            .then(function (response) {
          
             console.log("res", response.data);
             setText(response.data)
              })
             .catch(function (error) {
           
            console.log(error);
            })
      }

      useEffect(()=>{
          details()
      },[page])


    return<div>
        <h1>Student Records</h1>
        <button onClick={()=>{
            axios.get(`http://localhost:3001/patients?_sort=Age&_order=desc?_page=${page}&_limit=3`)
            .then(function (response) {
            
              console.log(response.data);
              setText(response.data)
            })
            .catch(function (error) {
           
              console.log(error);
            })
        }}
        
        >Sort-Age </button>
      
         {text?.map((el, i)=> 
         
         <div key={i} style={{
          border : "1px solid black",
             padding : "10px",
             marginLeft : "400px",
             marginRight : "400px",
             marginTop: "10px"
            
         }}>
             
             <h2>Name : {el?.name}</h2>
            
            <h5>Age : {el?.age} </h5>
            <h5>Gender : {el?.gender} </h5>
            <h5>medicines discripted : {el?.medicines} </h5>
            
         </div>
         
         
         )}
         <button onClick={()=>{
            setPage(page-1)
        }}
        >prev</button>
        <button onClick={()=>{
            setPage(page + 1)
        }}
        >next</button>
          
    </div>
}

export {Records}
